import {
  Actions
} from 'react-native-router-flux';
import axios from 'axios';
import {
  EMAIL_CHANGED,
  PASSWORD_CHANGED,
  LOGIN_USER_SUCCESS,
  LOGIN_USER_FAIL,
  LOGIN_USER
}
from '../actions/types';
import {
  host
} from '../../configs';


export const emailChanged = (text) => ({
    type: EMAIL_CHANGED,
    payload: text
  });

export const passwordChanged = (text) => ({
    type: PASSWORD_CHANGED,
    payload: text
  });

export const loginUser = ({
  email,
  password
}) => (dispatch) => {
    dispatch({
      type: LOGIN_USER
    });
    // GET profile info
    axios({
        method: 'get',
        url: `${host}/auth/profile`,
        auth: {
          username: email,
          password: password
        }
      })
      .then(user => {
        loginUserSuccess(dispatch, user.data);
      })
      .catch(() => loginUserFail(dispatch));
  };


const loginUserFail = (dispatch) => {
  dispatch({
    type: LOGIN_USER_FAIL
  });
};

const loginUserSuccess = (dispatch, user) => {
  dispatch({
    type: LOGIN_USER_SUCCESS,
    payload: user
  });
};

export const EMAIL_CHANGED = 'email_changed';
export const PASSWORD_CHANGED = 'password_changed';
export const LOGIN_USER_SUCCESS = 'login_user_success';
export const LOGIN_USER_FAIL = 'login_user_fail';
export const LOGIN_USER = 'login_user';

import React from 'react';
import {Text, TouchableOpacity} from 'react-native';

const Button = ({onPress, children, color}) => {
  const {purple_buttonStyle, blue_buttonStyle, blue_textStyle, purple_textStyle} = styles;
  switch (color) {
    case 'purple':
      this.buttonStyle = purple_buttonStyle;
      this.textStyle = purple_textStyle;
      break;
    case 'blue':
      this.buttonStyle = blue_buttonStyle;
      this.textStyle = blue_textStyle;
      break;
    default:
      this.buttonStyle = blue_buttonStyle;
      this.textStyle = blue_textStyle;
      break;
  }

  return (<TouchableOpacity onPress={onPress} style={this.buttonStyle}>
    <Text style={this.textStyle}>
      {children}
    </Text>
  </TouchableOpacity>);
};

const styles = {
  blue_textStyle: {
    alignSelf: 'center',
    color: '#007aff',
    fontSize: 16,
    fontWeight: '600',
    paddingTop: 5,
    paddingBottom: 10
