import React, { Component } from 'react';
import { View, StyleSheet, Text } from 'react-native';
import { Location } from 'expo';
import Mappa from './Mappa';
import { Card, Spinner } from '../common';

export default class Locator extends Component {
	state = {
		location: null,
		errorMessage: null,
		loading: true
	};

  componentWillMount() {
      this.getLocationAsync();
  }

  getLocationAsync = async () => {
    try {
      const location = await Location.getCurrentPositionAsync({});
      this.setState({ loading: true });
      if (location !== null) {
        this.setState({ location, loading: false });
      }
    } catch (e) {
      this.setState({ errorMessage: e, loading: false });
    }
  };

  renderError() {
    if (this.state.errorMessage) {
      return (
        <View style={styles.bubble}>
          <Card >
            <Text style={styles.paragraph}>Please turn on Location services and restart the app :) thank u</Text>
          </Card>
        </View>
      );
    }
  }

  renderSpinner() {
    if (this.state.loading) {
      return <Spinner />;
    }
  }

  render() {
    console.log(this.state);
    return (
      <View style={styles.container}>
            {this.renderError()}
            {this.renderSpinner()}
            {this.state.location !== null &&
            <Mappa location={this.state.location} />}
      </View>

    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
		marginTop: 24
  },
  paragraph: {
    margin: 24,
    fontSize: 18,
